}
input#createpass1, input#createpass2{
    text-transform:none!important
}
input#userpass, input#userpass-link{
    text-transform:none!important
}
.most-popular .post-byline {
    text-transform:none!important
}
#tny-overlay .credit {
    text-transform:none!important
}
article figure .credit {
    text-transform:none!important
}
.module.contributors h2, .the-latest-container h2{
    text-transform:none!important
}
#mobi-nav-link{
    font-size:63%!important;
    font-weight:normal!important;
}
.signin-sub,.modal-intro{
    font-weight:normal!important
}

.hamburger-dropdowns-navigation__flyout-link-text{
    font-weight:normal!important
}

.primary-nav>ul a {
    font-weight:normal!important;
}
article>header .title {
    margin: 5px 0 12px!important;
}
article>header .contributors, article>header .contributors+.comma{
    position:static!important;
    font-weight:normal!important;
    bottom:0;
}
article>header time {
    position: static!important;
}
.the-latest-container h2 {
    font-weight:normal!important
}
.header-ad .advertisement.loaded {
    margin-top: 15px;
    padding-bottom:24px;
}

