				title = title.replace(/</g, '&lt;').replace(/>/g, '&gt;');
			} catch(e) {}
			try {
				dek   = item.pagemap.metatags[0]['og:description'];
			} catch(e) {
				dek   = '';
			}
			try {
				image = item.pagemap.cse_thumbnail[0].src;
			} catch(e) {
				image = '';
			}
			try {
				if (item.pagemap.hcard) {
					contribs = item.pagemap.hcard;
				}
			} catch(e) {
				contribs = '';
			}
			

			if (date) {
				date = date.match(/(\d\d\d\d)-(\d\d)-(\d\d)/);
				if (!date) {
					date = '';
				}
				var year = date[1];
				var month = monthNames[parseInt(date[2]) - 1];
				var day = parseInt(date[3]);
				date = month + ' ' + day + ', ' + year;
				date = '<span class="timestamp">' + date + '</span>';
			}
			

			if (!dek) {
				dek = item.snippet;
			} else if (dek.length > 255) {
				var shortened = [];
				var words = dek.split(/\s+/);
				for (var j = 0; j < words.length; j++) {
					if (shortened.join(' ').length > 255) {
						dek = shortened.join(' ') + ' &hellip;';
						break;
					}
					shortened.push(words[j]);
				}
			}
			// Just show the first one until we get better schema from Google.
			if ( contribs.length ) {
				contribs = [ contribs[0] ];
			}
			if (contribs) {
				
				// var prefix = (item.link.indexOf('/podcast/') === -1) ? 'By ' : 'With ';
				contribs = '<h3>By <span>' + contribs[0].fn + '</span></h3>';
			}

			if (image) {
				image = '<img src="' + image + '" alt="' + title.replace('"', '&quot;') + '">';
				image = '<a href="' + item.link + '">' + image + '</a>';
				image = '<figure>' + image + '</figure>';
				articleClass = '';
			} else {
				image = '';
				articleClass = 'no-image';
			}

			if (dek) {
