									 '</span>';
	resultSort = '<div class="result-sort">' + resultSort + '</div>';
	var metaDiv = document.getElementById('search-meta');
	metaDiv.className = 'visible';
	metaDiv.innerHTML = resultCount + resultSort;

	var base = '?q=' + qEncoded;
	if (sort && sort == 'date') {
		base += '&sort=date';
	}

	if (response.searchInformation.totalResults > 0) {
		var pagination = '';
		var page = 1;
		if (start) {
			page = Math.floor((start - 1) / 10) + 1;
		} else {
			start = 1;
		}

		var startPage = Math.max(1, page - 5);
		var endPage = Math.ceil(response.searchInformation.totalResults / 10);
		var prevPage = '';
		var nextPage = '';
		if (startPage > 6 &&
				endPage - startPage < 10) {
			startPage = endPage - 9;
		}
		var pageOffset = 1;
		var selected = '';
		for (var p = startPage; p < startPage + 10; p++) {
			pageOffset = (p - 1) * 10 + 1;
			if (pageOffset > response.searchInformation.totalResults) {
				break;
			}
			if (p == page) {
				selected = ' class="selected"';
				if (page > 1) {
					var prevLabel = (sort == 'date') ? 'Newer' : 'Previous';
					prevPage = '<a href="' + base + '&start=' + (pageOffset - 10) + '" class="previous">' + prevLabel + '</a>';
				}
				if (page != endPage) {
					var nextLabel = (sort == 'date') ? 'Older' : 'Next';
					nextPage = '<a href="' + base + '&start=' + (pageOffset + 10) + '" class="next">' + nextLabel + '</a>';
				}
				var mobilePage = '<span class="mobile-page">Page ' + p + '</span>';
			} else {
				selected = '';
			}
			pagination += '<a href="' + base + '&start=' + pageOffset + '"' + selected + '>' + p + '</a>';
		}

		pagination = prevPage + mobilePage + pagination + nextPage;

		var paginationDiv = document.getElementById('search-pagination');
		paginationDiv.className = 'visible';
		paginationDiv.innerHTML = pagination;
	}

	var resultsOnPage = document.querySelectorAll('#search-results article');
	if (resultsOnPage.length < 5) {
		var lastAd = document.querySelector('.container-rail .advertisement:last-child');
		var parent = lastAd.parentNode;
		parent.removeChild(lastAd);
		var promo = document.querySelector('.container-rail .promo');
		parent = promo.parentNode;
		parent.removeChild(promo);
	}
}

